test.config:
indexer
{
	mem_limit			= 16M
}

searchd
{

	max_filter_values 	= 500000
	max_packet_size 	= 18M
	max_threads_per_query			= 4
	read_timeout			= 1
}

source src1
{
	type			= mysql


	sql_query		= SELECT id, group_id, title, mva FROM test_table
	sql_attr_uint	= group_id
	sql_attr_multi = uint mva from field
}

source src2 : src1
{
	sql_query		= SELECT 10+id, group_id, title, mva FROM test_table
}

source src_i1
{
	type			= mysql

	sql_query = SELECT id, title, group_id as gid FROM test_table
	sql_attr_uint	= gid
}

source src_i2
{
	type			= mysql

	sql_query = SELECT id + 100, title, group_id as gid FROM test_table
	sql_attr_uint	= gid
}

index block1
{
	source			= src1
	path			= /block1
	min_word_len	= 1
}



index block2
{
	source			= src2
	path			= /block2
	min_word_len	= 1
}

index dist_agent_2
{
	source			= src1
	path			= /dist_agent_2
}




index dist
{
	type					= distributed
	local					= block1
	agent					= :block2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index dist2
{
	type					= distributed
	agent					= :dist_agent_2
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000
}

index i1
{
	source			= src_i1
	path			= /i1
}

index i2
{
	source			= src_i2
	path			= /i2
}

index rt
{
	type = rt
	path			= /rt
	rt_mem_limit = 128k
	rt_field		= title
	rt_attr_uint	= gid
}
