test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

}

source srctest
{
	type			= mysql


	sql_query		= SELECT * FROM test_table
	sql_attr_uint	= gid
}

index exp_on
{
	source			= srctest
	path			= /test_on
	expand_keywords	= 1
	min_prefix_len = 2
}

index exp_off
{
	source			= srctest
	path			= /test_off
	min_prefix_len = 2
}

index rt
{
	type = rt
	path			= /expanded-rt
	dict = keywords
	expand_keywords	= 1
	min_infix_len = 2
	rt_field = title
	rt_attr_uint = idd	
}

index dist_on
{
	type					= distributed
	agent					= :exp_on
}

index dist_off
{
	type					= distributed
	agent					= :exp_off
}

index exp_1
{
	source			= srctest
	path			= /test_1
	morphology 		= stem_en
	index_exact_words = 1
}

index dist_1
{
	type					= distributed
	agent					= :exp_1
}
