{
	mem_limit		= 16M
}

searchd
{

}

source src
{
	type			= mysql

	sql_query		= SELECT *, 11 as gid FROM test_table

	sql_attr_uint	= gid
	sql_field_string = title
}

index idx
{
	source			= src
	path			= /idx
}

source src2 : src
{
	sql_query		= SELECT *, 11 as gid FROM test_table2
}

index idx2 
{
	source			= src2
	path			= /idx2
}


