test.config:
indexer
{
	mem_limit				= 16M
}

searchd
{
	
}

source src1
{
	type			= mysql
	
	sql_query		= select * from test_table where id<6
	sql_attr_uint	= gid
}

source src2
{
	type			= mysql
	
	sql_query		= select * from test_table where id=6
	sql_attr_uint	= gid

	sql_query_killlist = SELECT 1
}

index main1
{
	source			= src1
	path			= /main1
}

index main2
{
	source			= src1
	path			= /main2
}

index delta
{
	source			= src2
	path			= /delta
}

index delta2
{
	source			= src1
	path			= /delta2
	killlist_target = main1:kl
}

test.db_create:
CREATE TABLE `test_table` (
  `id` int(11) NOT NULL,
  `gid` int(11) NOT NULL,
  `title` varchar(255) NOT NULL
);
