test.config:
indexer
{
	mem_limit		= 16M
}

searchd
{

}

source src_bi
{
	type			= mysql

	sql_query		= select * from test_table
	sql_attr_uint	= gid
}

index idx_bi
{
	source = src_bi
	path			= /idx_bi

	bigram_index = all
	dict = keywords
	min_prefix_len = 2
}

index rt_bi
{
	type = rt
	path			= /rt_bi
	docinfo = extern
	rt_mem_limit = 128k

	rt_field		= title
	rt_attr_uint	= gid

	bigram_index = all
	dict = keywords
	min_prefix_len = 2
}

index dist_local
{
	type = distributed
	local = idx_bi
	local = rt_bi
}

index dist_remote
{
	type = distributed
	agent = :dist_local
	agent_connect_timeout	= 1000
	agent_query_timeout		= 3000	
}
